import React,{Component} from 'react';
import {Card,CardImg,CardBody,CardTitle,CardText,Breadcrumb,BreadcrumbItem,Button,
        Modal,ModalBody,ModalHeader,Label,Row,Col} from 'reactstrap';
import {Link} from 'react-router-dom';
import {Control,LocalForm,Errors} from 'react-redux-form';

    function RenderDish({dish}){
        return (
            <Card>
                <CardImg width="100%" src = {dish.image} alt = {dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        );
    }

   function RenderComments({comments}){
        var commentList = comments.map(comment =>{
            return(
                <li key = {comment.id}>
                    {comment.comment}
                    <br /><br />
                    -- {comment.author}&nbsp;&nbsp;{new Intl.DateTimeFormat('en-US',{year:'numeric',month:'short',day:'2-digit'}).format(new Date(Date.parse(comment.date)))}
                    <br /><br />
                </li>
            )
        });
        return(
            <div>
                <h4>Comments</h4>
                <ul className = "list-unstyled">
                    {commentList}
                </ul>
                <CommentForm />
            </div>
        );
    }

    const DishDetail = (props) => {
        if(props.dish){
            return(
                <div className = "container">
                    <div className = "row">
                        <Breadcrumb>
                            <BreadcrumbItem><Link to = '/menu'>Menu</Link></BreadcrumbItem>
                            <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                        </Breadcrumb>
                        <div className = "col-12">
                            <h3>{props.dish.name}</h3>
                            <hr />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-12 col-md-5 m-1">
                            <RenderDish dish = {props.dish} />
                        </div>
                        <div className="col-12 col-md-5 m-1">
                            <RenderComments comments = {props.comments} />
                        </div>
                    </div>
                </div>
            );
        }else{
            return(
                <div></div>
            );
        }
    }

export default DishDetail;

export class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            isModalOpen:false
        }
        this.toggleModal = this.toggleModal.bind(this);
    }
    toggleModal(){
        this.setState({
            isModalOpen:!this.state.isModalOpen
        })
    }
    render(){
        return(
            <div>
                <Button outline onClick = {this.toggleModal}>
                <span className = "fa fa-pencil fa-lg"></span>Submit Comment   
                </Button>
                <div class = "row row-content">
                <Modal isOpen = {this.state.isModalOpen} toggle = {this.state.toggleModal}>
                    <ModalHeader toggle = {this.toggleModal}>Submit Comment</ModalHeader>
                    <ModalBody>
                        <LocalForm>
                            <div className = "container">
                                <div className = "col-12">
                                    <Label>Rating</Label>
                                </div>
                                <div className = "col-12">
                                    <Control.select model = ".rating"  name = "rating" 
                                        className = "form-control"
                                    >
                                        <option>1</option>
                                        <option>2</option>
                                        <option>3</option>
                                        <option>4</option>
                                        <option>5</option>
                                    </Control.select>
                                </div>
                                <div className = "col-12">
                                    <Label htmlFor = "author" >Your Name</Label>
                                </div>
                                <div className = "col-12">
                                    <Control.text model = ".author" id = "author" name = "author"
                                        className = "form-control"/>
                                </div>
                                <div className = "col-12">
                                    <Label htmlFor = "comment">Comment</Label>
                                </div>
                                <div className = "col-12">
                                    <Control.textarea model = ".comment" id = "comment" name="comment"
                                        row = "20"
                                        className = "form-control"
                                    />
                                </div>
                                <Row className = "form-group">
                                    <Col md = {{size:10,offset:2}}>
                                        <Button type = "submit" color="primary">
                                            Send Feedback  
                                        </Button>
                                    </Col>
                                </Row>
                            </div>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </div>
        </div>
        );
    }
}
